import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import TextFieldGroup from "../common/TextFieldGroup";
import TextAreaFieldGroup from "../common/TextAreaFieldGroup";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addHelicopters } from "../../actions/profileActions";

class AddHelicopter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      helicopterName: "",
      helicopterLocation: "",
      helicopterAvailableFrom: "",
      helicopterAvailableTill: "",
      guestCapacity: "",
      helicopterArea: "",
      helicopterImage: "",
      helicopterDescription: "",
      helicopterPrice: "",
      helicopterAddedBy: "",
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  onSubmit(e) {
    e.preventDefault();

    const helicopterData = {
      helicopterName: this.state.helicopterName,
      helicopterLocation: this.state.helicopterLocation,
      helicopterAvailableFrom: this.state.helicopterAvailableFrom,
      helicopterAvailableTill: this.state.helicopterAvailableTill,
      guestCapacity: this.state.guestCapacity,
      helicopterArea: this.state.helicopterArea,
      helicopterImage: this.state.helicopterImage,
      helicopterDescription: this.state.helicopterDescription,
      helicopterPrice: this.state.helicopterPrice,
      helicopterAddedBy: this.state.helicopterAddedBy
    };

    this.props.addHelicopters(helicopterData, this.props.history);
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    const { errors } = this.state;

    return (
      <div className="add-helicopters">
        <div className="container">
          <div className="row">
            <div className="col-md-5 m-auto">
              <Link to="/owner-helicopters" className="btn btn-light">
                Go Back
              </Link>
              <h2 className="display-8 text-center">Add Helicopter</h2>
              <p className="lead text-center">
                Add the helicopter you want to make available for Traveller
              </p>

              <form onSubmit={this.onSubmit}>
                <TextFieldGroup
                  placeholder="Helicopter Name"
                  name="helicopterName"
                  value={this.state.helicopterName}
                  onChange={this.onChange}
                  error={errors.helicopterName}
                />
                <TextFieldGroup
                  placeholder="Helicopter Location or City Name"
                  name="helicopterLocation"
                  value={this.state.helicopterLocation}
                  onChange={this.onChange}
                  error={errors.helicopterLocation}
                />
                <TextFieldGroup
                  type="number"
                  min="1"
                  placeholder="Helicopter Guest Capacity"
                  name="guestCapacity"
                  value={this.state.guestCapacity}
                  onChange={this.onChange}
                  error={errors.guestCapacity}
                />
                <h6>Helicopter Available From Date</h6>
                <TextFieldGroup
                  name="helicopterAvailableFrom"
                  type="date"
                  value={this.state.helicopterAvailableFrom}
                  onChange={this.onChange}
                  error={errors.helicopterAvailableFrom}
                />
                <h6>Helicopter Available Till Date</h6>
                <TextFieldGroup
                  name="helicopterAvailableTill"
                  type="date"
                  value={this.state.helicopterAvailableTill}
                  onChange={this.onChange}
                  error={errors.helicopterAvailableTill}
                />

                <TextFieldGroup
                  type="number"
                  placeholder="Helicopter Price"
                  name="helicopterPrice"
                  value={this.state.helicopterPrice}
                  onChange={this.onChange}
                  error={errors.helicopterPrice}
                  info="Helicopter Price must be per ride in USD"
                />

                <TextFieldGroup
                  type="number"
                  placeholder="Helicopter Area"
                  name="helicopterArea"
                  value={this.state.helicopterArea}
                  onChange={this.onChange}
                  error={errors.helicopterArea}
                  info="Helicopter Area must in Square feet"
                />

                <TextFieldGroup
                  placeholder="Helicopter Image Url"
                  name="helicopterImage"
                  value={this.state.helicopterImage}
                  onChange={this.onChange}
                  error={errors.helicopterImage}
                  info="Helicopter Image field should be an image URL"
                />

                <TextAreaFieldGroup
                  placeholder="Helicopter Description"
                  name="helicopterDescription"
                  value={this.state.helicopterDescription}
                  onChange={this.onChange}
                  error={errors.helicopterDescription}
                  info="Tell us about your Helicopter"
                />
                <input
                  type="submit"
                  value="Submit"
                  className="btn btn-info btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

AddHelicopter.propTypes = {
  addHelicopters: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile,
  errors: state.errors
});

export default connect(mapStateToProps, { addHelicopters })(
  withRouter(AddHelicopter)
);
